new Vue({el:"#app",vuetify:new Vuetify,data:()=>({name:void 0,email:void 0,phone:void 0,type:void 0,contents:void 0,form:!1,inputdata:!1,makesure:!0,rules:{required:value=>!!value||"必須項目です",email:value=>{const pattern=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;return pattern.test(value)||"正しい形式で入力してください"},phone:value=>{const pattern=/^(0{1}\d{1,4}-{0,1}\d{1,4}-{0,1}\d{4})$/;return pattern.test(value)||"7桁以上の半角数字で入力してください"}},loading:!1,snackbar:!1,snackbarText:void 0,snackbarError:!1}),methods:{clickBtnSend(){this.loading=!0;let params=new URLSearchParams;params.append("name",this.name),params.append("email",this.email),params.append("tel",this.phone),params.append("type",this.type),params.append("message",this.contents),axios.post("forms/mail.php",params).then(response=>{this.snackbarText=response.data,this.snackbar=!0,this.loading=!1,this.inputdata=!1,this.makesure=!0,this.$refs.form.reset()}).catch(error=>{this.snackbarError=!0,console.log("error")})}}});